import image from './images/*.png';

class ImageSlider {
  constructor(slideNum, ...images) {
    this.images = images;
    this.slideNum = slideNum;
    return this.createSlider(this.slideNum, ...this.images);
  }

  createSlider(num, ...image) {
    const slider = document.createElement('div');
    const dots = document.createElement('div');
    const prev = document.createElement('a');
    const next = document.createElement('a');

    slider.className = 'slider';
    prev.className = 'prev';
    dots.className = 'dots';
    next.className = 'next';

    prev.innerHTML = '&#10094;';
    next.innerHTML = '&#10095;';

    let number = 0;

    for (let i = 1; i <= num; i++) {
      const div = document.createElement('div');
      const dot = document.createElement('span');
      const img = document.createElement('img');

      dot.className = 'dot';
      div.className = 'mySlides fade';
      img.src = image[number];
      div.appendChild(img);
      dots.appendChild(dot);
      slider.append(div, prev, next, dots);

      number++;
    }
    return slider;
  }
}

const slider1 = new ImageSlider(3, image['1'], image['2'], image['3']);

