class Product {
  constructor({ image: src, model: model, price: price }) {
    this.src = src;
    this.model = model;
    this.price = price;
    this.createProduct();
  }

  createProduct() {
    const container = document.querySelector('.container');
    const div = document.createElement('div');
    const img = document.createElement('img');
    const p = document.createElement('p');

    div.className = 'items';

    img.src = this.src;
    p.innerHTML =
      'Model:  ' + this.model + '<br>' + 'Price:  ' + this.price + ' kr';
    div.append(img, p);
    container.appendChild(div);
  }
}

